<?phpif ( function_exists('register_sidebar') )    register_sidebars(1);	remove_filter('get_the_excerpt', 'wp_trim_excerpt');add_filter('get_the_excerpt', 'custom_trim_excerpt');add_theme_support( 'post-thumbnails' );set_post_thumbnail_size( 150, 150 ); // 150 pixels wide by 150 pixels tall, hard crop modefunction custom_trim_excerpt($text) { // Fakes an excerpt if neededglobal $post;if ( '' == $text ) {$text = get_the_content('');$text = apply_filters('the_content', $text);$text = str_replace(']]>', ']]>', $text);$text = strip_tags($text);$excerpt_length = 90;$words = explode(' ', $text, $excerpt_length + 1);if (count($words) > $excerpt_length) {array_pop($words);array_push($words, '...');$text = implode(' ', $words);}}return $text;}
?>